'use strict';

var fs = require('fs');
var express = require('express');
var app = express();

if (!process.env.DISABLE_XORIGIN) {
  app.use(function(req, res, next) {
    var allowedOrigins = ['https://narrow-plane.gomix.me', 'https://www.freecodecamp.com'];
    var origin = req.headers.origin || '*';
    if(!process.env.XORIG_RESTRICT || allowedOrigins.indexOf(origin) > -1){
         console.log(origin);
         res.setHeader('Access-Control-Allow-Origin', origin);
         res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");
    }
    next();
  });
}

app.use('/public', express.static(process.cwd() + '/public'));

app.route('/api/package.json')
  .get(function(req, res, next) {
    console.log('requested');
    fs.readFile(__dirname + '/package.json', function(err, data) {
      if(err) return next(err);
      res.type('txt').send(data.toString());
    });
  });

app.route('/api/getTime').get( function(req, res, next ){
  console.log('api/getTime');
  var dateObject = {}
  if( req.query.time ){
    console.log( "api gettime : time =  " + JSON.stringify( req.query.time ) );
    var date;
    if( parseInt( req.query.time ) )
    {
      var timeStamp = parseInt( req.query.time ) * 1000;
      console.log( "timestamp : " +  timeStamp );
      date = new Date( timeStamp );
    }
    else{
      date = new Date(req.query.time);
    }
    dateObject.unix = date.getTime() / 1000
    dateObject.natural =  date.toString()
  }
  else
  {
    dateObject.unix = null
    dateObject.natural = null
  }
  res.type('json').send( JSON.stringify( dateObject ) )
})

app.route('/api/whoami').get( function(req, res, next ){
  console.log( req.headers )
  var ip = req.headers['x-forwarded-for'].split(",")[0]
  var language = req.headers['accept-language'].split(",")[0]
  var software = req.headers['user-agent']
  var clientInfo = {}
  clientInfo.ipaddress = ip
  clientInfo.language = language
  clientInfo.software = software
  res.type('json').send( JSON.stringify( clientInfo ) )
})
  
app.route('/')
    .get(function(req, res) {
		  res.sendFile(process.cwd() + '/views/index.html');
    })


// Respond not found to all the wrong routes
app.use(function(req, res, next){
  res.status(404);
  res.type('txt').send('Not found');
});

// Error Middleware
app.use(function(err, req, res, next) {
  if(err) {
    res.status(err.status || 500)
      .type('txt')
      .send(err.message || 'SERVER ERROR');
  }  
})

app.listen(process.env.PORT, function () {
  console.log('Node.js listening ...');
});

